<?php

namespace Spicy\RankingBundle\Entity\Repository;

use Doctrine\ORM\EntityRepository;
use Doctrine\ORM\Tools\Pagination\Paginator;
use Spicy\RankingBundle\Entity\RankingType;
use Spicy\RankingBundle\Entity\Ranking;

/**
 * NewsRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class RankingRepository extends EntityRepository
{
    public function getLastRanking() 
    {
        $qb = $this->createQueryBuilder('r')
                ->leftJoin('r.videoRankings', 'vr')
                ->leftJoin('vr.video', 'v')
                ->leftJoin('v.genre_musicaux', 'g', 'WITH', 'g.id<> :id_retro')
                ->addOrderBy('vr.nbVu','DESC')
                ->addSelect('vr')
                ->setParameter('id_retro', 2)
                ->andWhere("r.dateRanking in (select max(ra.dateRanking) from SpicyRankingBundle:Ranking ra)"); 
        
        return $qb->getQuery()->getOneOrNullResult();
    }
    
    public function getPreviousRanking(Ranking $ranking) 
    {
        $qb = $this->createQueryBuilder('r')
                ->leftJoin('r.videoRankings', 'vr')
                ->leftJoin('vr.video', 'v')
                ->leftJoin('v.genre_musicaux', 'g', 'WITH', 'g.id<> :id_retro')
                ->addOrderBy('r.dateRanking','DESC')
                ->addSelect('vr')
                ->setParameter('id_retro', 2)
                ->andWhere("r.id in (select max(ra.id) from SpicyRankingBundle:Ranking ra "
                        . "where ra.id<".$ranking->getId()." AND ra.rankingType=".$ranking->getRankingType()->getId().")"); 
        
        return $qb->getQuery()->getOneOrNullResult();
    }
    
    public function getRankings($page,$nbOccurences,$type=RankingType::MOIS)
    {
        $qb = $this->createQueryBuilder('r')
                ->leftJoin('r.videoRankings', 'vr')
                ->leftJoin('vr.video', 'v')
                ->leftJoin('v.genre_musicaux', 'g', 'WITH', 'g.id<> :id_retro')
                ->leftJoin('v.type_videos', 't', 'WITH', 't.id<> :id_type')
                ->leftJoin('r.rankingType', 'rt')
                ->andWhere('rt.id=:type')
                ->orderBy('r.id','DESC')
                ->setParameter('id_retro', 2)
                ->setParameter('id_type', 1)
                ->setParameter('type', $type)
                ->setFirstResult(($page-1)*$nbOccurences)
                ->setMaxResults($nbOccurences);
        
        $query=$qb->getQuery();
        return new Paginator($query);
    }
    
    public function getOne($id)
    {
        $qb = $this->createQueryBuilder('r')
                ->join('r.videoRankings', 'vr')
                ->join('vr.video', 'v')
                ->join('v.genre_musicaux', 'g', 'WITH', 'g.id<> :id_retro')
                ->join('v.type_videos', 't', 'WITH', 't.id= :id_type')
                ->where('r.id=:id')
                ->setParameter('id', $id)
                ->setParameter('id_retro', 2)
                ->setParameter('id_type', 1)
                ->addSelect('vr')
                ->addSelect('v')
                ->addSelect('g')
                ->addSelect('t');
        
        return $qb->getQuery()->getOneOrNullResult();
    }
    
    public function getByDate($type=  RankingType::MOIS) 
    {
        $now=new \DateTime("now");
        
        $qb = $this->createQueryBuilder('r')
                ->leftJoin('r.rankingType', 'rt')
                ->leftJoin('r.videoRankings', 'vr')
                ->leftJoin('vr.video', 'v')
                ->leftJoin('v.genre_musicaux', 'g', 'WITH', 'g.id<> :id_retro')
                ->leftJoin('v.type_videos', 't', 'WITH', 't.id<> :id_type')
                ->where('r.startRanking<:now')
                ->andWhere('r.endRanking>:now')
                ->andWhere('rt.id=:type')
                ->addOrderBy('r.dateRanking','DESC')
                ->addOrderBy('vr.nbVu','DESC')
                ->setParameter('id_retro', 2)
                ->setParameter('id_type', 1)
                ->setParameter('now', $now)
                ->setParameter('type', $type);
        
        return $qb->getQuery()->getOneOrNullResult();
    }
    
    public function getLast() 
    {
        $qb = $this->createQueryBuilder('r')
                ->where('r.id in (select max(ra.id) from SpicyRankingBundle:Ranking ra)');
        
        return $qb->getQuery()->getOneOrNullResult();
    }
    
    public function getRankingsByType($nbOccurences,$type=RankingType::MOIS)
    {
        $qb = $this->createQueryBuilder('r')
                ->leftJoin('r.rankingType', 'rt')
                ->where('rt.id=:type')
                ->orderBy('r.id','DESC')
                ->setParameter('type', $type)
                ->setFirstResult(1)
                ->setMaxResults($nbOccurences);
        
        return $qb->getQuery()->getResult();
    }
}
